class DetalleResumen extends BaseDetalleResumen
{
	public function setUp()
  {
		parent::setUp();
		$this->hasOne('Lote', array(
		 'local' => 'nro_lote',
		 'foreign' => 'nro_lote',
		 'onDelete' => 'RESTRICT'));
	}
	
  public function SimboloMoneda(){
    return $this->getMoneda()->getSimbolo();
  }
  
  public function PrecioFormato(){
    return sprintf($this->SimboloMoneda()." %01.2f", $this->getPrecio());
  }
  
  public function TotalFormato(){
    return sprintf($this->SimboloMoneda()." %01.2f", $this->getTotal());
  } 
  
  public function IvaFormato(){
    return sprintf($this->SimboloMoneda()." %01.2f", $this->getIva());
  } 
  
  public function SubTotalFormato(){
    return sprintf($this->SimboloMoneda()." %01.2f", $this->getSubTotal());
  }
	
	public function RemitoProductoCantVend(){
		// ventas asociadas al remito
		$vendidos = Doctrine::getTable('DetalleResumen')->findByDetRemitoId($this->id);
		$total_vta = 0;
		foreach($vendidos as $vta) {
			// devoluciones asociadas a las ventas asociadas al remito
			$dev_a_remito = Doctrine::getTable('DevProducto')->findByResumenId($vta->resumen_id);
			$total_dev_r = 0;
			foreach($dev_a_remito as $dev_r) $total_dev_r += $dev_r->cantidad;
			$total_vta += $vta->cantidad - $total_dev_r;
		}
		return $total_vta;
	}
	
	public function RemitoProductoCantDev(){
		$devueltos = Doctrine::getTable('DevProducto')->findByResumenIdAndProductoIdAndNroLote($this->resumen_id, $this->producto_id, $this->nro_lote);
		$total_dev = 0;
		foreach($devueltos as $dev) $total_dev += $dev->cantidad;
		return $total_dev;
	}
	
	public function getPorcentajeComision() {
		$comision = Doctrine::getTable('DescuentoZona')->findByClienteId($this->getResumen()->cliente_id);
		if (empty($comision[0])) {
			$comision = Doctrine::getTable('DescuentoZona')->findByGrupoprodId($this->getProducto()->grupoprod_id);
			if (empty($comision[0])) {
				$comision = Doctrine::getTable('DescuentoZona')->findByProductoId($this->producto_id);
				if (empty($comision[0])) {
					return 'No Definida';
				}
			}
		}
		return $comision[0]->porc_desc;
	}

	public function getComision() {
		$comision = Doctrine::getTable('DescuentoZona')->findByClienteId($this->getResumen()->cliente_id);
		if (empty($comision[0])) {
			$comision = Doctrine::getTable('DescuentoZona')->findByGrupoprodId($this->getProducto()->grupoprod_id);
			if (empty($comision[0])) {
				$comision = Doctrine::getTable('DescuentoZona')->findByProductoId($this->producto_id);
				if (empty($comision[0])) {
					return 0;
				}
			}
		}
		return $this->sub_total * $comision[0]->porc_desc / 100;
	}
	
	public function getTipoDesc() {
		if ($this->getTipoDescuento() == 1) 
			return 'Bonif.';
		else 
			return '';
	}
}