class FactCompra extends BaseFactCompra
{
  public function __toString()
  {
    return $this->getProveedor()." - ".$this->getNumero();
  }
  
  public function getTotalFactura()
  {
    $suma = 0;
    foreach($this->getDetalle() as $det){
      $suma += $det->getTotal();
    }
    return $suma;
  }
}