<?php

/**
 * ResumenTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ResumenTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object ResumenTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Resumen');
    }
    
    function construct(){
        $this->setOption('orderBy','fecha DESC');
    }    
    
    public function retrieveConJoins(Doctrine_Query $q){
			$zid = sfContext::getInstance()->getUser()->getGuardUser()->getZonaId();
      $rootAlias = $q->getRootAlias();
      $q->leftJoin($rootAlias . '.Cliente c');
      $q->leftJoin($rootAlias . '.TipoVenta tv');
      $q->leftJoin($rootAlias . '.TipoFactura tf');
			$q->andWhere($rootAlias . '.zona_id = '.$zid);
      $q->orderBy($rootAlias . '.fecha desc');
      return $q;
    } 
		
		public function getRemitosVta(){
			$query = Doctrine_Core::getTable('Resumen')
			->createQuery('q')
			->where('q.tipofactura_id = 4')
			//->andWhere('not exists(select id from resumen where resumen.remito_id = r.id)')
			->orderBy('fecha desc, nro_factura desc');
			$result = $query->execute();
			return $result;
		}
		
		public function getRemitos(){
			$query = Doctrine_Core::getTable('Resumen')
			->createQuery('q')
			->where('q.tipofactura_id = 4')
			->orderBy('fecha desc, nro_factura desc');
			$result = $query->execute();
			return $result;
		}	
		
		public function getRemitosParaCompra(){
			$usuario = sfContext::getInstance()->getUser()->getGuardUser()->getId();
			$uz = Doctrine_Core::getTable('UsuarioZona')->findByUsuario($usuario);
			$zona = Doctrine_Core::getTable('Zona')->find($uz[0]->zona_id);
			
			$query = Doctrine_Core::getTable('Resumen')
			->createQuery('q')
			->leftJoin('q.Compra c')
			->where('q.tipofactura_id = 4')
			->andWhere('q.cliente_id = '.$zona->cliente_id)
			->andWhere('c.remito_id is null')
			->andWhere("q.fecha >  '2019-05-15'")
			->orderBy('fecha desc, nro_factura desc');
			$result = $query->execute();
			return $result;
		}
		
	public function getVentas($p_zona_id=1) {
		$sql = "select count(id) as cantidad from resumen where zona_id = $p_zona_id and fecha >= date_format(curdate(), '%Y-%m-01')";
		$con = Doctrine_Manager::getInstance()->connection();
		$st = $con->execute($sql);
		$resultado = $st->fetch(PDO::FETCH_OBJ);
		return $resultado->cantidad;
	}
	
	public function getVentasPedidos($p_zona_id=1) {
		$sql = "
			select count(id) as cantidad 
			from resumen 
			where 
				pedido_id is not null
				and zona_id = $p_zona_id 
				and fecha >= date_format(curdate(), '%Y-%m-01')
		";
		$con = Doctrine_Manager::getInstance()->connection();
		$st = $con->execute($sql);
		$resultado = $st->fetch(PDO::FETCH_OBJ);
		return $resultado->cantidad;
	}
}
